let projDataList = { // Implemented as table instead of array so it's easier to keep track of the IDs
    0: {
        title: "GitHub User Page",
        imgsrc: "https://i.imgflip.com/5h33ht.jpg",
        info: (
        <>
          <div className="project-info-title">GitHub User Page</div>
          <p>This is the project you're using right now! It gives info about me and some of my personal projects. It was created using JavaScript, React, HTML (JSX), and CSS.</p>
        </>),
    },
    1: {
        title: "Pre-Babel Chat",
        imgsrc: "https://i.ytimg.com/vi/1IR5SZkSDIM/oar2.jpg?sqp=-oaymwEVCJUDENAFSFryq4qpAwcIARUAAIhC&rs=AOn4CLBwawcn-UFVdO8DOGttTeoGC2qnng",
        info: (<p>It updates in real time!</p>),
    },
    2: {
        title: "Linear Algebra Canvas",
        imgsrc: "https://i.pinimg.com/236x/0b/be/c8/0bbec85a04bdb120588cb477e85a7021.jpg",
        info: (<p>Obviously!</p>),
    },
  };
  
export default class ProjectState {
    constructor() {
      this.projId = 0;
      this.stateData = projDataList[0];
      this.projDataList = projDataList;
      this.subscriber = null;
    }
  
    set projNum(id) {
      if(~~projDataList[id]) {
        console.error("Attempted to display project #" + id + ", which does not exist.");
        return;
      }

      this.projId = id;
      this.stateData = projDataList[id];

      if(this.subscriber) {
        this.subscriber();
      }
    }

    get projNum() {
      return this.projId;
    }

    get state() {
        return this.stateData;
    }

    addInfoSubscriber(subscriber) {
      this.subscriber = subscriber;
    }
  }
